mapper.select:
SELECT /*+ index_desc(qna) */ qna_no, title, text, qna_date, edit, img_check, qna_result, user_no FROM qna WHERE user_no = #{cri.user_no}
AND qna_result = #{cri.category}
OFFSET (#{cri.currPage} - 1) * #{cri.amount} ROWS FETCH NEXT #{cri.amount} ROWS ONLY

SELECT /*+ index_desc(qna) */ count(qna_no)
FROM qna
WHERE user_no = #{cri.user_no}

AND qna_result = #{cri.category}

SELECT /*+ index_desc(report) */ report_no, title, text, report_date, edit, img_check, report_result, user_no FROM report WHERE user_no = #{cri.user_no}
AND report_result = #{cri.category}
OFFSET (#{cri.currPage} - 1) * #{cri.amount} ROWS FETCH NEXT #{cri.amount} ROWS ONLY

SELECT /*+ index_desc(report) */ count(report_no)
FROM report
WHERE user_no = #{cri.user_no}

AND report_result = #{cri.category}
