mapper.select:
SELECT /*+ LEADING(t1) USE_NL(t3 t2) index_desc(t1 pk_comm) */ t1.comm_no, t1.board_no, t1.text, t1.comm_date, t1.edit, t1.parent_no, t1.user_no, t2.title, t3.category_name FROM comm t1 INNER JOIN board t2 ON t1.board_no = t2.board_no
INNER JOIN category t3 ON t2.category_no = t3.category_no
WHERE t1.user_no = #{cri.user_no}
AND t2.category_no = #{cri.category}
AND REGEXP_LIKE(t1.text, #{cri.keyword}, 'i')
OFFSET (#{cri.currPage}-1) * #{cri.amount} ROWS FETCH NEXT #{cri.amount} ROWS ONLY

SELECT /*+ index_desc(t1 pk_comm) */ count(t1.comm_no) FROM comm t1 INNER JOIN board t2 ON t1.board_no = t2.board_no WHERE t1.user_no = #{cri.user_no}
AND t2.category_no = #{cri.category}
AND REGEXP_LIKE(t1.text, #{cri.keyword}, 'i')
